// INIT
var bebop = require("../.");
var drone = null;

function doDrone() {
try {
iLog("Starting Drone Boot Sequence");

drone = bebop.createClient();
iLog("Drone Client Created");

iLog("Attempting to connect to Drone");
drone.connect(function() {
iLog("Connected with Drone");
initDroneEvents();
initDroneFlightPath();
iLog("Drone Flight Path Initiated");
});
} catch (consume) {
iLog("error in doDrone() " + consume);
landDrone();
}
return true;
}
exports.doDrone = doDrone;

// METHODS
async function initDroneFlightPath() {

try {
drone.takeOff();
await pauseExecution("take-off", 4000);
drone.stop();
drone.down(10);
await pauseExecution("lowering", 2000);
drone.stop();
await pauseExecution("stop", 1000);
/*drone.forward(20);
await pauseExecution("forward", 1000);
drone.stop();
await pauseExecution("stop", 1000);
drone.backward(20);
await pauseExecution("backward", 1000);
drone.stop();
await pauseExecution("stop", 1000);
*/
//drone.right(10);
//drone.clockwise(10);
//await pauseExecution("clockwise", 2000);
//drone.stop();
//await pauseExecution("stop", 1000);
drone.counterClockwise(30);
await pauseExecution("counterClockwise", 2000);
drone.stop();
//await pauseExecution("stop", 1000);
landDrone();
drone.land();
await pauseExecution("landing", 1000);
iLog("Drone done");
process.exit(1);
} catch(consume) {
iLog("something crashed");
drone.stop();
drone.land();
iLog(consume);
}
}

function landDrone() {
try{
drone.stop();
drone.land();
} catch(consume) {
iLog("Error in landing drone");
drone.stop();
drone.land();
iLog(consume);
}
}
exports.landDrone = landDrone;

function initDroneEvents(){
 drone.on("battery", function (data) {
    iLog("Drone Battery Level: " + data);
  });
}

function pauseExecution(vLog, timeMS){

iLog(" >> Drone Data: " +  vLog);
return new Promise(resolve => setTimeout(resolve, timeMS));
}

function iLog(vMsg) {
try{
console.log(new Date().format('Ymd His') + ": " + vMsg);
} catch(consume) {
console.log("error in iLog land --> " + consume);
}
}

function sleep(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}
